// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;
import "../client/node_modules/@openzeppelin/contracts/access/Ownable.sol";

// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";

contract WhistleBlower is Ownable {
    constructor() public {
        address owner;
        owner = msg.sender;
    }

    struct organisation {
        string orgName; //name of the organisation
        string orgType; //NGO, Activists, Lawyers
        address orgAddress; //to allow access to organ to WhistleBlower
        string url; //organisation Website url
        uint256 id; //to map in organisationMapping
    }

    struct Case {
        string title; //title of whistleBlow
        string description; //description of case
        string proof; //proof of whistleBlow
        uint256 validityCount; //verify validity
        mapping(address => bool) restrictValidityCount;
        string url; //link to website
        address blowingOrg; //one who is whistleBlowing
        uint256 id;
    }

    uint256 organisationMappingLength; //total no of organisations
    uint256 caseMappingLength; //total no of cases on the platform

    mapping(uint256 => organisation) public organisationMapping;
    mapping(address => bool) organisationLogin;
    mapping(uint256 => Case) public caseMapping;
    mapping(address => uint256) public orgCaseCount; //no of cases a particular org has blown

    modifier isOrganisation() {
        require(organisationLogin[msg.sender], "You are not an organisation!");
        _;
    }

    function isAnOrganisationLogin(address _address)
        public
        view
        returns (bool)
    {
        return organisationLogin[_address];
    }

    function registerOrganisation(
        string memory _orgName,
        string memory _orgType,
        address _orgAddress,
        string memory _url,
        uint256 _id
    ) public onlyOwner {
        organisationMapping[_id].id = _id;
        organisationMapping[_id].orgName = _orgName;
        organisationMapping[_id].orgType = _orgType;
        organisationMapping[_id].orgAddress = _orgAddress;
        organisationMapping[_id].url = _url;
        organisationMappingLength++;
        organisationLogin[_orgAddress] = true;
    }

    function createCase(
        string memory _title,
        string memory _description,
        string memory _proof,
        address _blowingOrg,
        string memory _url,
        uint256 _id
    ) public isOrganisation {
        caseMapping[_id].id = _id;
        caseMapping[_id].title = _title;
        caseMapping[_id].description = _description;
        caseMapping[_id].proof = _proof;
        caseMapping[_id].blowingOrg = msg.sender;
        caseMapping[_id].url = _url;
        orgCaseCount[_blowingOrg]++;
        caseMappingLength++;
    }

    //increasing validity count by getting approvals from different accounts;
    function approveValidity(uint256 _id) public {
        require(
            caseMapping[_id].restrictValidityCount[msg.sender] == false,
            "You have already approved this case validity!"
        );
        caseMapping[_id].validityCount++;
        caseMapping[_id].restrictValidityCount[msg.sender] = true;
    }
}
